
public class Circunferencia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double radio = 3;
		double pi = Math.PI;
		double resultadoArea = pi*Math.pow(radio, 2);
		double resultadoPerimetro = 2*pi*radio;

		
		
		System.out.println("El area de la circunferencia es: " + resultadoArea + "\n" + "El perimetro de la circunferencia es: " + resultadoPerimetro);
		

		
	}

}
